sp_widget.client_script:
api.controller=function($scope, $window, $http, spUtil, $location) {
	var c = this;

	console.log("CLIENT: ", $scope.data);

	// Extract 'game_id' from the URL
	var searchObject = $location.search();
	c.gameId = searchObject.game_id;


	// playerList
	c.playerList = []; 
    c.loadPlayers = function() {
        var gameId = c.gameId

        $http.get('/api/x_snc_nowtrivia/game/' + gameId + '/players')
					.then(function(response) {
            c.playerList = response.data.result; 
        }, function(error) {
            console.error('Error fetching players:', error);
        });
    };
    // Load players when the widget loads
    c.loadPlayers();


	// Generate join game URL
	c.joinGameUrl = 'https://' + $window.location.host + '/nowtrivia?id=join-game&game_id=' + c.gameId;

	//COPY LINK TO CLIPBOARD
	c.copyLinkToClipboard = function() {
		navigator.clipboard.writeText(c.joinGameUrl)
			.then(function() {
			spUtil.addTrivialMessage('Link copied to clipboard successfully!');
		}, function(err) {
			spUtil.addErrorMessage('Could not copy text: ', err);
		});
	}


	var query = "";
    console.log("query = " + query);


	// x_snc_nowtrivia_test
		spUtil.recordWatch($scope, "x_snc_nowtrivia_test",
										 "", 
										 function(payload) {
    console.log("Update in x_snc_nowtrivia_test for game " + c.gameId + ": ", payload);

	});


	spUtil.recordWatch($scope, "x_snc_nowtrivia_player",
										 query, 
										 function(payload) {
    console.log("Update in players for game " + c.gameId + ": ", payload);

	});

		spUtil.recordWatch($scope, "x_snc_nowtrivia_player",
										 "game.sys_id=ad37d2158743f11031a07558dabb3577", 
										 function(payload) {
		console.log("game.sys_id=ad37d2158743f11031a07558dabb3577 payload=", payload); 

	});
};

